
public class Adding_function {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		SumAll(100,70);
	    car(45,789,677,46);
	
	
	}



public static void SumAll (int f, int y)// argument condition
{
	
	int sum= f-y;  //local variable
	System.out.println(sum);
	
	
}


public static void car(int a, int b,int c, int d){
	
	int car= a+b+c+d;
	System.out.println(car);
	
	
}



}
