
public class Overloading {


public static void main(String[] args) {
	// TODO Auto-generated method stub

numbers(566666666);
country(55.76);



}



public static void numbers (int i){   //Function

System.out.println(i); //action


}

public static void numbers(long u){
	
System.out.println(u);
	
	
}

//overloading functions are two different functions with the same name but different arguments.


public static void country(int y){
	
	
	System.out.println(y);
	
}


public static void country(double u){
	
	
	System.out.println(u);
}



}
